import React, { useState, useEffect } from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { useNavigate } from "react-router-dom";

import userPro from "./user.png";

const Navbar = () => {
  const navigate = useNavigate();
  let isLoggedIn=false;
  const curr = localStorage.getItem("user");
  const [dropDown,setDropdown] = useState(true);
  let ProfileLink = "../profile/" + curr;

  const handleDropdown =  () => {
       setDropdown(!dropDown)
       console.log(dropDown);  
  }

  if (curr) {
    isLoggedIn=true;
    console.log("heyyyy");
    console.log(curr);
  }

  const handleClicklogin = async (e) => {
    navigate("../login");
  };

  const handleClicklogout = async (e) => {
    localStorage.removeItem("user");
    navigate("../home");
  };

  return (
    <div className="sticky h-2/12 top-0 z-50 bg-blue-100 flex justify-between items-center">
      {/* Rounded icon */}
      <div className="pl-10">
        <a href="/home" className="text-2xl ">
          Mango
        </a>
      </div>

      <div className="flex mr-3 w-4/12">
        <a className="mr-5 text-2xl " href="/home">
          Home
        </a>
        <a className="ml-5 text-2xl mr-5">Premium</a>
        <div className="relative w-3/12">
          <div
            className="relative ml-5 w-full"
            onMouseEnter={handleDropdown}
            onMouseLeave={handleDropdown}
          >
            <a className="text-2xl">Services</a>
            {dropDown && (
              <div className="absolute pl-1 w-full">
                <div className=" hover:bg-green-900">web</div>
                <div>Web Developer</div>
                <div>App Developer</div>
                <div>Logo Making</div>
                <div>ER Diagrams</div>
                <div>Software Architecture</div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="p-4">
        {isLoggedIn ? (
          <div className="flex justify-center items-center">
            <div className="flex flex-col items-center">
              <img
                src={userPro}
                alt="Profile"
                className="rounded-full h-12 w-14 cursor-pointer pr-4 mb-2"
              />
              <a className="text-blue-900 underline" href={ProfileLink}>
                {curr}
              </a>
            </div>
            <button
              className="ml-4 text-white bg-gray-800 hover:bg-gray-700 pl-1 rounded"
              onClick={(e) => handleClicklogout(e)}
            >
              Log Out
            </button>
          </div>
        ) : (
          <button
            className="h-12 m-4 px-4 text-white bg-gray-800 hover:bg-gray-700  rounded"
            onClick={(e) => handleClicklogin(e)}
          >
            Log In
          </button>
        )}
      </div>
    </div>
  );
};

export default Navbar;
