import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import { useNavigate, useParams } from "react-router-dom";
import Pro from "./user.png";

const Developers = () => {
  const [items, setItems] = useState([]);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/developers/" + id, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
        });
        const data = await response.json();
        if (data.result.length === 0) {
          alert(data.message);
          navigate("/home");
        } else {
          setItems(data.result);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [id]);

  return (
    <div>
      <Navbar />
      <div className="container bg-dark-subtle mt-5 py-5 border border-warning">
        <div className="text-center">
          <h1>Web Developers</h1>
        </div>
        <div className="row row-cols-1 row-cols-md-2 row-cols-lg-4 justify-content-center">
          {items.map((item) => (
            <div key={item._id} className="col mb-4">
              {/* <div className="card border-warning h-100">
                <div className="card-body">
                  <h5 className="card-title">{item.name}</h5>
                  <p className="card-text">
                    Rating:{" "}
                    {[...Array(item.rating)].map((_, index) => (
                      <span key={index} className="text-warning">
                        â˜…
                      </span>
                    ))}
                  </p>
                  <p className="card-text">Username: {item.username}</p>
                </div> */}

                <div className="card w-18rem" >
                  <img
                    src={Pro}
                    alt="Profile"
                    className="card-img-bottom"
                    style={{ maxWidth: "100%", height: "auto" }}
                  />
                  <div class="card-body">
                    <h5 className="card-title">{item.name}</h5>
                    <p class="card-text">
                      Some quick example text to build on the card title and
                      make up the bulk of the card's content.
                    </p>
                  </div>
                  <ul class="list-group list-group-flush">
                    <li class="username">item.username</li>
                    <li class="Rating">item.rating</li>
                    <li class="list-group-item">A third item</li>
                  </ul>
                  {/* <div class="card-body">
                    <a href="#" class="card-link">
                      Card link
                    </a>
                    <a href="#" class="card-link">
                      Another link
                    </a>
                  </div> */}
                </div>

              </div>

           
          ))}
        </div>
      </div>
    </div>
  );
};

export default Developers;
