import React, { useState } from "react";

const Login = () => {
  const [formData , setFormData] = useState({});

  // console.log(formData)

  const handleClick = async (e) => {

    e.preventDefault();

    try{

      const response = await fetch("http://localhost:5000/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),//send syntax
      });

      const data = await response.json();
      if(!response.ok){
        alert(data.message)
      }
      alert(data.message)

    }
    catch(error){
      console.log(error);
      alert('Could not submit data')
    }
    
  }


  return (

      <div className="h-screen flex justify-center items-center  bg-white-300">
        <div className="bg-black p-20 rounded-lg">
          <h2 className="text-2xl font-bold text-white mb-8">Login</h2>
          <div className="m-2">
            <label htmlFor="email" className="block text-blue-700 mb-2">
              Email:
            </label>
            <input
              type="text"
              id="email"
              onChange={(e) => setFormData({
                ...formData , [e.target.id] : e.target.value
              })}
              className=" mt-1  rounded-md border-gray-300" 
            />
          </div>
          <div className="m-2">
            <label htmlFor="passw" className="block text-blue-700 mb-2">
              Password:
            </label>
            <input
              type="password"
              id="passw"
              onChange={(e) => setFormData({
                ...formData , [e.target.id] : e.target.value
              })}
              className=" mt-1 rounded-md border-gray-300"
            />
          </div>
          <div className="flex justify-center">
            <button className="bg-blue-500 m-5 px-2 py-1 text-white font-bold rounded focus:outline-none focus:shadow-outline" onClick={(e) => handleClick(e)}>
              Login
            </button>
          </div>
        </div>
      </div>
  );
};

export default Login;
