import React, { useState, useEffect } from "react";
import axios from "axios";

const Profile = () => {
  const [user, setUser] = useState(null);
  const [editing, setEditing] = useState(false);
  const [editedFields, setEditedFields] = useState({
    username: "",
    name: "",
    email: "",
    skills: [],
  });

  useEffect(() => {
    axios
      .get("/api/user/profile")
      .then((response) => {
        setUser(response.data);
        // Initialize editedFields state with current user data
        console.log(response.data);
        setEditedFields(user);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });
  }, []);

  const handleFieldChange = (e) => {
    const fieldName = e.target.id;
    const fieldValue = e.target.value;
    setEditedFields({ ...editedFields, [fieldName]: fieldValue });
  };

  const handleClick = async (e) => {
    e.preventDefault();

    try {
      console.log("hi");
      const response = await fetch("http://localhost:5000/signup", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(editedFields), //send syntax
      });

      const data = await response.json();
      if (!response.flag) {
        alert(editedFields.message);
        navigate("/login"); //navigate to login page
      } else {
        alert(editedFields.message);
      }
    } catch (error) {
      console.log(error);
      alert("Could not submit data");
    }
  };

  return (
    <div className="h-screen bg-gray-800 flex justify-center items-center">
      <div className="bg-white shadow-md rounded px-8 pt-6 pb-8 mb-4 w-5/12">
        <h2 className="text-2xl mb-4 font-bold text-gray-800">Edit Profile</h2>

        <div className="mb-4">
          <label
            className="block text-gray-700 text-sm font-bold mb-2"
            htmlFor="username"
          >
            Username
          </label>
          <input
            type="text"
            id="username"
            className="appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
            value={editedFields.username}
            onChange={(e) => handleFieldChange(e)}
          />
        </div>

        <div className="mb-4">
          <label
            className="block text-gray-700 text-sm font-bold mb-2"
            htmlFor="name"
          >
            Name
          </label>
          <input
            type="text"
            id="name"
            className=" border rounded w-full py-2 px-3 text-gray-700  focus:outline-none focus:shadow-outline"
            value={editedFields.name}
            onChange={(e) => handleFieldChange(e)}
          />
        </div>

        <div className="mb-4">
          <label
            className="block text-gray-700 text-sm font-bold mb-2"
            htmlFor="email"
          >
            Email
          </label>
          <input
            type="email"
            id="email"
            className=" border rounded w-full py-2 px-3 text-gray-700 focus:outline-none focus:shadow-outline"
            value={editedFields.email}
            onChange={(e) => handleFieldChange(e)}
          />
        </div>

        <div className="mb-6">
          <label
            className="block text-gray-700 text-sm font-bold mb-2"
            htmlFor="skills"
          >
            Skills
          </label>
          <input
            type="text"
            id="skills"
            className=" border rounded w-full py-2 px-3 text-gray-700  focus:outline-none focus:shadow-outline"
            value={editedFields.skills}
            onChange={(e) => handleFieldChange(e)}
          />
        </div>

        <div className="flex items-center justify-between">
          <button
            className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
            type="button"
            onClick={(e) => handleClick(e)} // Placeholder for saveChanges function
          >
            Save Changes
          </button>
        </div>
      </div>
    </div>
  );
};

export default Profile;
