import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import userPro from "./user.png";

const Navbar = () => {
  const navigate = useNavigate();

  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const currUser = localStorage.getItem("user");
  const profileLink = currUser ? `../profile/${currUser}` : "";

  const handleClickLogin = () => {
    navigate("../login");
  };

  const handleClickLogout = () => {
    localStorage.removeItem("user");
     navigate("../home");
  };

  // Check if user is logged in
  if (currUser) {
    setIsLoggedIn(true);
  }

  return (
    <nav className="fixed top-0 w-full bg-blue-100 flex justify-between items-center z-10">
      {/* Rounded icon */}
      <div className="pl-10">
        <a href="/home" className="text-2xl ">
          Mango
        </a>
      </div>

      <div className="flex mr-3">
        <a className="mr-5 text-2xl " href="/home">
          Home
        </a>
        <div className="ml-5 text-2xl">Premium</div>
      </div>

      <div className="p-4">
        {isLoggedIn ? (
          <div className="flex justify-center items-center">
            <div className="flex flex-col items-center">
              <img
                src={userPro}
                alt="Profile"
                className="rounded-full h-14 w-14 cursor-pointer pr-4 mb-2"
              />
              <a className="text-blue-900 underline" href={profileLink}>
                {currUser}
              </a>
            </div>
            <button
              className="ml-4 text-white bg-gray-800 hover:bg-gray-700 pl-1 rounded"
              onClick={handleClickLogout}
            >
              Log Out
            </button>
          </div>
        ) : (
          <button
            className="h-14 m-4 px-4 text-white bg-gray-800 hover:bg-gray-700  rounded"
            onClick={handleClickLogin}
          >
            Log In
          </button>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
