import React, { useState, useEffect } from "react";
import { Navigate, useNavigate, useParams } from "react-router-dom";
import Navbar from "./Navbar";



const ViewAssignProject = () => {

  const [data, setData] = useState({});
  const { id } = useParams();

  const handleClick = () => {
           
  };
 useEffect (()=>{
       const fetchData = async () => {
         try {
           const response = await fetch(
             `http://localhost:5000/developers/${id}`,
             {
               method: "POST",
               headers: {
                 "Content-Type": "application/json",
               },
             }
           );
           const data = await response.json();
           if (data.result.length === 0) {
             alert(data.message);
             navigate("/home");
           } else {
             setItems(data.result);
           }
         } catch (error) {
           console.error("Error fetching data:", error);
         }
       };

       fetchData();
 })
  const handleInput = (e) => {
    setData({ ...data, [e.target.id]: e.target.value }); // Send the data to the backend ....
  };

  return (
    <div>
      <Navbar />

      <div className="mx-auto container  mt-8">
        {" "}
        {/*mx-auto */}
        <h2 className="text-2xl font-bold mb-4">Assign Project</h2>
        <div className="flex flex-col">
          {/* Project Name */}
          <div className="mb-4">
            <label
              htmlFor="projectName"
              className="block text-sm font-bold mb-2"
            >
              Project Name:
            </label>

            <div
              type="text"
              id="projectName"
              className="border border-gray-300 rounded py-2 px-3 w-full"
              placeholder="Enter project name"
              onChange={(e) => handleInput(e)}
            />
          </div>

          {/* Deadline */}

          <div className="mb-4">
            <label htmlFor="deadline" className="block text-sm font-bold mb-2">
              Deadline:
            </label>
            <div
              id="deadline"
              className="border border-gray-300 rounded py-2 px-3 w-full"
              onChange={(e) => handleInput(e)}
            />
          </div>
          
          {/* {cost} */}
          <div className="mb-4">
            <label htmlFor="cost" className="block text-sm font-bold mb-2">
              Cost:
            </label>
            <div
              id="cost"
              className="border border-gray-300 rounded py-2 px-3 w-full"
              onChange={(e) => handleInput(e)}
            />
            </div>

          {/* Project Demands */}
          <div className="mb-4">
            <label
              htmlFor="projectDemands"
              className="block text-sm font-bold mb-2"
            >
              Project Demands:
            </label>
            <div
              id="projectDemands"
              className="border border-gray-300 rounded py-2 px-3 w-full"
              rows="4"
              placeholder="Enter project demands"
              onChange={(e) => handleInput(e)}
            >

            </div>

          </div>

        </div>


        
      </div>
    </div>
  );
};

export default ViewAssignProject;
