import React, { useState } from "react";
import { useHistory } from "react-router-dom"; // Import useHistory hook
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import Navbar from "./Navbar";

function Home() {
  const [searchQuery, setSearchQuery] = useState("");
  const history = useHistory();

  const handleInputChange = (event) => {
    setSearchQuery(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Navigate to a different route with searchQuery as a parameter
    history.push(`/search?query=${searchQuery}`);
  };

  return (
    <>
      <Navbar />
      <div className="relative">
        <Carousel infiniteLoop useKeyboardArrows autoPlay showThumbs={false}>
          <div className="">
            <img
              className="h-full w-full object-cover"
              src="./img3.jpg"
              alt="Slide 1"
            />
          </div>
          <div className="">
            <img
              className="h-full w-full object-cover"
              src="./img2.jpg"
              alt="Slide 2"
            />
          </div>
          <div className="">
            <img
              className="h-full w-full object-cover"
              src="./img1.jpg"
              alt="Slide 3"
            />
          </div>
        </Carousel>
        <div className="absolute top-1/4 left-20 pl-20 pt-10 rounded-md w-4/12 transform -translate-y-1/2">
          <div className="text-5xl pb-4">
            Find the right freelance service, right away
          </div>
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              placeholder="Search..."
              value={searchQuery}
              onChange={handleInputChange}
              className="w-full bg-gray-200 px-5 py-2 rounded-md outline-none"
            />
            <button
              type="submit"
              className="mt-2 bg-blue-500 text-white px-4 py-2 rounded-md"
            >
              Search
            </button>
          </form>
        </div>
      </div>
    </>
  );
}

export default Home;
