import React, { useState } from "react";
import userIcon from "./user.png";
import editField from "./profile";
import { Carousel } from "react-responsive-carousel";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { useNavigate } from "react-router-dom";
function Home() {
      const navigate = useNavigate();
  let [isLogedIn, setLogin] = useState(false);
   const curr= localStorage.getItem('user');
 const useEffect=(()=>{
        if (localStorage.getItem("user") ) {
            isLogedIn = true;
        }
    },[]);
 
   const handleClicklogin= async (e)=>{
    navigate('../login');
 };
  return (
    <div className="h-screen bg-red-800">
      <div className="h-4/12 bg-green-800 flex justify-between items-center">
        {/* Rounded icon */}
        <div className="pl-10">
          <a href="/">Mango</a>
        </div>

        <div className="flex mr-3">
          <div className="mr-5 ">Home</div>
          <div className="ml-5">Premium</div>
        </div>

        <div className="p-4">
          {isLogedIn ? (
            <>
              <img
                src={userIcon}
                alt="Profile"
                className="rounded-full h-14 w-14 cursor-pointer pr-4"
              />
              <div className="flex">
                <div className="mr-2">
            
                  {curr}
                </div>
                <div className="ml-4">Log Out</div>
              </div>
            </>
          ) : (
            <div className="h-16 mt-6" onClick={(e) => handleClicklogin(e)}>
              Log In
            </div>
          )}
        </div>
      </div>

      <div className="relative">
        <Carousel infiniteLoop useKeyboardArrows autoPlay showThumbs={false}>
          <div className="h-60vh">
            <img className="h-full w-full object-cover" src="./img3.jpg" />
          </div>
          <div className="h-60vh">
            <img className="h-full w-full object-cover" src="./img2.jpg" />
          </div>
          <div className="h-60vh">
            <img className="h-full w-full object-cover" src="./img1.jpg" />
          </div>
        </Carousel>
        <div className="absolute top-1/4 left-20 pl-20 pt-10 rounded-md w-4/12 transform -translate-y-1/2">
          <div className="text-5xl pb-4">
            Find the right freelance service, right away
          </div>
          <input
            type="text"
            placeholder="Search..."
            className="w-full bg-gray-200 px-5 py-2 rounded-md outline-none"
          />
        </div>
      </div>
    </div>
  );
}

export default Home;
