import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import { useNavigate, useParams } from "react-router-dom";
import Pro from "./user.png";

const Developers = () => {
  const [items, setItems] = useState([]);
  const { id } = useParams();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch("http://localhost:5000/developers/" + id, {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
        });
        const data = await response.json();
        if (data.result.length === 0) {
          alert(data.message);
          navigate("/home");
        } else {
          setItems(data.result);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [id]);

  // Function to chunk the items array into groups of 4
  const chunkArray = (arr, chunkSize) => {
    const chunks = [];
    for (let i = 0; i < arr.length; i += chunkSize) {
      chunks.push(arr.slice(i, i + chunkSize));
    }
    return chunks;
  };

  // Divide the items into groups of 4 cards each
  const chunkedItems = chunkArray(items, 4);

  return (
    <div>
      <Navbar />
      <div className="container bg-dark-subtle mt-5 py-5 border border-warning">
        <div className="text-center">
          <h1>Web Developers</h1>
        </div>
        {/* Loop through each chunk of items */}
        {chunkedItems.map((chunk, index) => (
          <div key={index} className="row mb-4">
            {/* Loop through items in the current chunk */}
            {chunk.map((item) => (
              <div key={item._id} className="col-md-3 mb-4">
                <div className="card h-100 shadow">
                  <img
                    src={Pro}
                    alt="Profile"
                    className="card-img-top"
                    style={{ height: "200px", objectFit: "cover" }}
                  />
                  <div className="card-body">
                    <h5 className="card-title">{item.name}</h5>
                    <p className="card-text">
                      Rating:{" "}
                      {[...Array(item.rating)].map((_, index) => (
                        <span key={index} className="text-warning">
                          â˜…
                        </span>
                      ))}
                    </p>
                    <p className="card-text">Username: {item.username}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Developers;
