import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import { useParams } from "react-router-dom";

const Profile = () => {
  console.log("ahosuifhui");
  const navigate = useNavigate();
  const [Data, handleData] = useState({
    name: "",
    username: "",
    email: "",
    password: "",
    skills: [],
  });
   

  useEffect(() => {
    axios
      .get("/api/user/profile")
      .then((response) => {
        setUser(response.data);
        // Initialize editedFields state with current user data
        console.log(response.data);
        setEditedFields(user);
      })
      .catch((error) => {
        console.error("Error fetching user data:", error);
      });
  }, []);
 



   let { id } = useParams();
  const handleSkill = (e) => {
    const ski = e.target.value.split(",");
    handleData({ ...Data, skills: ski });
  };
  
  const handleClick = async (e) => {
    e.preventDefault();

    try {
      if (localStorage.getItem("user")==id) {
        // alert("Please Login first");
        navigate("/myprofile/");
      }
      const user = localStorage.getItem("user");
      const response = await fetch("http://localhost:5000/myprofile/" + user, {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(Data), //send syntax
      });

      const data = await response.json();
      console.log(data.result.toString());
    } catch (error) {
      console.log(error);
      alert("Could not submit data");
    }
    // data.result mein user hai
    //if data.flag==0  => lancer hai
    // if data.flag==1 => client hai
    // if data.flag==2 => 404 hai
  };

  return (
    <div className="h-screen bg-white-900 flex justify-center items-center">
      <div className="w-4/12 bg-black rounded-lg p-8 shadow-lg">
        <h2 className="text-3xl font-bold text-white mb-8 text-front">
          Sign Up
        </h2>
        <div className="mb-4">
          <label htmlFor="Name" className="block text-white mb-2">
            Name *
          </label>
          <input
            type="text"
            id="name"
            onChange={(e) =>
              handleData({ ...Data, [e.target.id]: e.target.value })
            }
            className="rounded-md bg-gray-700 w-full p-2 text-white"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="Username" className="block text-white mb-2">
            Usernameeeee *
          </label>
          <input
            type="text"
            id="username"
            onChange={(e) =>
              handleData({ ...Data, [e.target.id]: e.target.value })
            }
            className="rounded-md bg-gray-700 w-full p-2 text-white"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="Email" className="block text-white mb-2">
            Email *
          </label>
          <input
            type="text"
            id="email"
            onChange={(e) =>
              handleData({ ...Data, [e.target.id]: e.target.value })
            }
            className="rounded-md bg-gray-700 w-full p-2 text-white"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="Password" className="block text-white mb-2">
            Password *
          </label>
          <input
            type="password"
            id="password"
            onChange={(e) =>
              handleData({ ...Data, [e.target.id]: e.target.value })
            }
            className="rounded-md bg-gray-700 w-full p-2 text-white"
          />
        </div>
        <div className="mb-4">
          <label htmlFor="Skills" className="block text-white mb-2">
            Skills (optional)
          </label>
          <input
            type="text"
            id="skills"
            onChange={handleSkill}
            className="rounded-md bg-gray-700 w-full p-2 text-white"
          />
        </div>
        <div className="flex justify-end">
          <button
            className="bg-blue-500 hover:bg-blue-600 px-4 py-2 rounded-md text-white"
            onClick={(e) => handleClick(e)}
          >
            Sign Up
          </button>
        </div>
        <div className="text-center mt-4">
          <p className="text-white">
            Already have an account?{" "}
            <a href="#" className="text-blue-500">
              Log in
            </a>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Profile;
